mapper.select:
SELECT

FROM ai_user_quota
WHERE user_id = #{userId,jdbcType=VARCHAR}

    AND tenant = #{tenant,jdbcType=VARCHAR}

LIMIT 1

SELECT distinct
FROM ai_user_quota
ORDER BY ${orderByClause}

mapper.update:
UPDATE ai_user_quota
user_id = #{userId,jdbcType=VARCHAR}, tenant = #{tenant,jdbcType=VARCHAR}, daily_limit = #{dailyLimit,jdbcType=BIGINT}, monthly_limit = #{monthlyLimit,jdbcType=BIGINT}, daily_used = #{dailyUsed,jdbcType=BIGINT}, monthly_used = #{monthlyUsed,jdbcType=BIGINT}, daily_reset_date = #{dailyResetDate,jdbcType=DATE}, monthly_reset_date = #{monthlyResetDate,jdbcType=DATE}, total_cost = #{totalCost,jdbcType=DECIMAL}, status = #{status,jdbcType=TINYINT}, create_time = #{createTime,jdbcType=BIGINT}, update_time = #{updateTime,jdbcType=BIGINT},
WHERE id = #{id,jdbcType=VARCHAR}
